<div><h3>DOE type</h3><p><input type="radio" name="type" value="2f" checked="checked" onclick='change_type(0)' > 2-levels-factorial<br><input type="radio" name="type" value="ff" onclick='change_type(1)' > full-factorial</p><h3>Number of factors</h3><p><select id="nbf_sel" onchange='nbr_change()'><?php	for($i=1;$i<=20;$i++){		echo "<option value='".$i."'  ";			if($i==2){				echo " selected ";			}		echo ">".$i."</option>";	}?></select></p><h3>Number of replicates</h3><p><select id="rep_sel" onchange='calculate_runs()'><?php 	for($i=1;$i<=20;$i++){		echo "<option value='".$i."' ";		if($i==2){			echo " selected ";		}		echo ">".$i."</option>";	}?></select></p></div><div id='levels_2_gen'>	<h3>Number of generators</h3>	<p><span id="gen_selector"><select id="gen_select" onchange='calculate_runs()'><option value='0'>0</option></select></span></p>	<h3>Center points</h3>	<p><select id="center_points" onchange='calculate_runs()'><?php		for($i=0;$i<=20;$i++){			echo "<option value='".$i."' >".$i."</option>";		}	?></select></p></div><div id='levels_selector' style="display:none;"><h3>Number of levels</h3><?php $level_sel = "";	for($i=2;$i<=20;$i++){		$level_sel .= "<option value='".$i."'  " ;		if($i==2){			$level_sel .= " selected ";		}		$level_sel .= ">".$i."</option>";	}	for($i=1;$i<=20;$i++){		echo "<span id='sel_level_".$i."'>Factor ".$i.": <select id='sel_l_".$i."' onchange='calculate_runs()'>".$level_sel."</select><br></span>";	}?></div><script>var nb_poss_gen = [0,0,1,2,3,4,4,5,6,7,8];var type_doe = 0;function generate_selector(nb){	stringhtml = "<select id='gen_select' onchange='calculate_runs()'>";	for(i = 0; i <= nb_poss_gen[nb-1]; i++) { 		stringhtml += "<option value='"+i+"'>"+i+"</option>";	}		stringhtml += "</select>";	$("#gen_selector").html(stringhtml);}function calculate_runs(){	if(type_doe == 0){ 		var nb_gen = parseInt($("#gen_select").val());		if(nb_gen==0){			var divider = 1;		}		else{			var divider = Math.pow(2,nb_gen);			console.log(divider)		} 		nb_runs = parseInt($("#rep_sel").val())*Math.pow(2,parseInt($("#nbf_sel").val()))/divider + parseInt($("#center_points").val()); 		if( parseInt($("#rep_sel").val())>1 ){			max_interaction = parseInt($("#nbf_sel").val()) - nb_gen;		}		else{			max_interaction = 0;		} 		}		else if(type_doe == 1){		 		 var nb_runs = 1;		 		 for(i=1; i <= parseInt($("#nbf_sel").val()) ; i++){		  		  nb_runs = nb_runs * parseInt($("#sel_l_"+i).val());		  		 }		 		 nb_runs = nb_runs * parseInt($("#rep_sel").val());		 		 		 if( parseInt($("#rep_sel").val())>1 ){			max_interaction = parseInt($("#nbf_sel").val());		 }		 else{			max_interaction = 0;		 }		 		}	console.log(nb_runs);}function change_type(i){	type_doe = i;	if(i==0){	 $("#levels_selector").hide();	 $("#levels_2_gen").show();	}	else if(i==1){	 $("#levels_2_gen").hide();	 $("#levels_selector").show();	}	calculate_runs();}function nbr_change(){	var i = parseInt($("#nbf_sel").val());	generate_selector(i);	for(j=1;j<=20;j++){	 if(j<=i){	  $("#sel_level_"+j).show();	 }	 else{	  $("#sel_level_"+j).hide();	 }	}		calculate_runs();}</script>